// page init
jQuery(function(){
	initCycleCarousel();
	initOpenClose();
	FontResize.init();
	initSameHeight();
	initMultiColumns();
	initScalingNavigation();
	initAnchorLinks();
	initFadeInBtn();
	initResponsiveFontResize();
});

// init responsive font resize
function initResponsiveFontResize(){
	var defaultSize = 1.5;
	var tabletSize = 1.3;
	var mobileSize = 1.7;

	// handle layout resize
	ResponsiveHelper.addRange({
		'..767': {
			on: function() {
				FontResize.options.defaultFS = mobileSize;
				FontResize.init();
			}
		},
		'768..1023': {
			on: function() {
				FontResize.options.defaultFS = tabletSize;
				FontResize.init();
			}
		},
		'1024..': {
			on: function() {
				FontResize.options.defaultFS = defaultSize;
				FontResize.init();
			}
		}
	});
}

// init fadein btn on scroll
function initFadeInBtn(){
	var win = jQuery(window);
	jQuery('.btn-top').each(function(){
		var btn = jQuery(this);
		function checkScroll(){
			if(win.scrollTop() > 0){
				btn.css({opacity:1});
			} else {
				btn.css({opacity:0});
			}
		}
		win.on('scroll load orientationchange', checkScroll);
	});
}

// init anchor links
function initAnchorLinks(){
	var animSpeed = 1000;
	var page = jQuery('html, body');
	var isTouchDevice = /MSIE 10.*Touch/.test(navigator.userAgent);
	jQuery('a.btn-top').each(function(){
		var link = jQuery(this);
		var href = link.attr('href');
		var indexHash = href.indexOf('#');
		if(indexHash < 0){
			return;
		}
		var target = jQuery(href.substr(indexHash));
		if(!target){
			return;
		}
		function scrollPage(e){
			e.preventDefault();
			if(isTouchDevice){
				page.scrollTop(target.offset().top);
			} else {
				page.stop().animate({scrollTop:target.offset().top},animSpeed);
			}
		}
		link.on('click',scrollPage);
	});
}

// cycle scroll gallery init
function initCycleCarousel() {
	jQuery('.cycle-gallery').scrollAbsoluteGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		stretchSlideToMask: true,
		maskAutoSize: true,
		autoRotation: true,
		switchTime: 4000,
		animSpeed: 500
	});
}

// open-close init
function initOpenClose() {
	jQuery('.open-close').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.item-box').sameHeight({
		elements: '.item',
		flexible: true,
		multiLine: true
	});
}

// split content into columns
function initMultiColumns() {
	jQuery('.article-content').columnize({
		buildOnce: true,
		columns: 2
	});
}

// resize navigation items to fill parent
function initScalingNavigation() {
	initAutoScalingNav({
		menuId: 'nav',
		sideClasses: true,
		flexible: true
	});
}

